using System;
using System.Collections.Generic;
using System.Text;

namespace Utility
{
    public class ChineseHelper
    {
        // Fields
        private const string strInitial = "";

        // Methods
        public ChineseHelper() { }
        public static string GetInitial(string strText)
        {
            if ((strText == null) || (strText.Length == 0))
            {
                return strText;
            }
            StringBuilder strResult = new StringBuilder();
            foreach (char vChar in strText)
            {
                if ((vChar >= 'һ') && (vChar <= 0x9fa5))
                {
                    strResult.Append("".Substring(vChar - 'һ', 1));
                }
                else if ((vChar > '\0') && (vChar <= '\x007f'))
                {
                    strResult.Append(vChar);
                }
            }
            return strResult.ToString();
        }

    }
}
